import React,{useContext,useState} from "react";
import AutocompleteSearchBar from "./AutoCompleteSearchBar";
import './Home.css'
import './Finddoctor.css'
import { NavLink } from "react-router-dom";
import { Navigate, useNavigate } from "react-router-dom";
import { Logincontext } from './ContextProvider';
import { ToastContainer, toast } from 'react-toastify';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './DatePicker.css';
// import DatePicker from './DatePicker';
// import TimePicker from './TimePicker'
// import BookingConfirmation from './BookingConfirmation'
import Select from 'react-select';
import './TimePicker.css'

const timeOptions = [
  { value: '09:00 AM', label: '09:00 AM' },
  { value: '10:00 AM', label: '10:00 AM' },
  { value: '11:00 AM', label: '11:00 AM' },
  // Add more time options as needed
];




const ScheduleAppointments = () => {
    const navigate=useNavigate();
    const [showModal, setShowModal]=useState(false)
    const { account, setAccount } = useContext(Logincontext);


//Date and Time picker
 const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState(null);
  const [bookingConfirmed, setBookingConfirmed] = useState(false);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const handleBooking = () => {
    // Here, you can implement booking logic (e.g., send data to the server)
    const [userData, setUserData] = useState({
    username: "",
    password: "",
    fName:'',
    lName:'',
    contact:'',
    email:'',
    address:''

  })
  const adddata = (e) => {
    const { name, value } = e.target;
    // console.log(name,value);

    setUserData(() => {
      return {
        ...userData,
        [name]: value
      }
    })
  };
  const senddata = async (e) => {
    e.preventDefault();

    const { D_id,startTime,endTime } = userData;
    try {
      const res = await fetch("http://localhost:3001/addSlot", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
            D_id,startTime,endTime
        })
      });

      const data = await res.json();
      console.log(data);

      if (res.status === 422 || !data) {
        toast.error("Invalid Details , Try Again", {
          position: "top-center"
        });
      } else {
        setUserData({
          ...userData,
          D_id:"",
          startTime:"",
          endTime:""

        });
        toast.success("Slot added Successfully", {
          position: "top-center"
        });
        // navigate("/login")
      }
    } catch (error) {
      console.log("front end error" + error.message);
    }
  }
    setBookingConfirmed(true);
  };

  return (
    <>
    {account?(
    <div>
      <div class="py-20 h-1/2 px-2 space-y-10 bg-gray-900 text-white">
        <h1 className="text-2xl text-center font-semibold doctor-title">Schedule your appointment slots here</h1>





        <>
        <div>
      <span className='dateSelectHeading'>Select a Date</span>
      <DatePicker
        selected={selectedDate}
        onChange={handleDateChange}
        dateFormat="MM/dd/yyyy"
        minDate={new Date()}
      />
    </div>
    <div>
      <span className='timeSelectHeading'>Select a Time</span>
      <Select className='timePickerSelectList'
        options={timeOptions}
        value={selectedTime}
        onChange={handleTimeChange}
      />
    </div>
          {selectedDate && selectedTime && (
            <div>
            <h2>Booking Confirmation</h2>
            <p>Date: {selectedDate.toLocaleDateString()}</p>
            <p>Time Slot : {selectedTime}</p>
            <button onClick={handleBooking}>Add slot</button>
            </div>
          )}
        </>


      </div>

      </div>
    ):( 

        <>
          <div
            className="  justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
          >
            <div className="relative w-auto my-6 mx-auto max-w-3xl">
              {/*content*/}
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                {/*header*/}
                <div className="flex items-start justify-between p-5 border-b border-solid border-slate-200 rounded-t">
                  <h3 className="text-3xl text-black ">
                    Login Please
                  </h3>
                  <button
                    className="p-1 ml-auto bg-transparent border-0 text-black font-extralight opacity-40 float-right text-3xl leading-none  outline-none focus:outline-none"
                    onClick={() => setShowModal(false)}
                  >
                    <NavLink to="/login"><p className='text-black'>x</p></NavLink>
                  </button>
                </div>

                <div className="relative p-6 flex flex-row space-x-4">

                  <p className="my-4 text-slate-500 text-lg leading-relaxed">
                    You must Login first in order to use our services
                  </p>
                </div>

                <div className="flex items-center justify-center p-6 border-t border-solid border-slate-200 rounded-b">
                  <button
                    className="bg-slate-900 text-slate-300 rounded-xl hover:text-slate-900 hover:bg-slate-300 font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={ () => setShowModal(false)}
                  >
                    <NavLink to="/login">Login</NavLink>

                  </button>

                </div>
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) }
    </>
  )
}

export default ScheduleAppointments
